
public class ControlFlowOperations {

	public static void main(String[] args) {
		
	// TODO Auto-generated method stub

		
		int number = 12; 
	
		
		if (number > 0 ) {
			System.out.println("bigger than 0 "); 
		} else
		{

			System.out.println("smaller than or equal 2 0  "); 
		 
	}
	
		int intayger = -90;
	
	if (intayger > 0 ) {
		System.out.println("bigger than 0 "); 
	} else 	if (intayger >= -99 ); {
		System.out.println("smaller than or equal 2 99 "); 
	} {
		System.out.println("smaller than or equal 2 0  "); 
	}

	

	
	
	
	//loops
	
	// for loop 
 
	for( int num1 = 0; num1 <= 10;num1++) { 
		System.out.println("Fruity Loops"); 
	}
	
	
	
	// while loop 
	int i = 0;
	while (i < 10) {
		i++;
		System.out.println("Yeah shoot em !!!!!!!! "); 
	}
	
	
	
	
	
   // do while 
	int number1 = 1;
	do {  
	System.out.println(number1 + "The numbers"  ); ++number1;  } 
	while (number1 < 10 );  
}};












